import { Component, OnInit } from '@angular/core';
import { ReservaService } from './services/reserva.service';
import { Reserva } from './models/reserva';
import { NgForm } from '@angular/forms';
import areIntervalsOverlapping from 'date-fns/areIntervalsOverlapping';
import parseISO from 'date-fns/parseISO';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  reserva = {} as Reserva;
  reservas: Reserva[] = [];

  constructor(private reservaService: ReservaService){}

  ngOnInit(): void {
    this.findAllReservas();
  }

  saveReserva(form: NgForm) {
    if(this.reserva.id !== undefined) {
      this.reservaService.update(this.reserva).subscribe(() => {
        this.cleanForm(form);
      });
    } else {
      this.createReserva(form);
    }
  }

  createReserva(form: NgForm) {
    if(this.verificaDatas()) {
      this.reservaService.create(this.reserva).subscribe(() => {
        this.cleanForm(form);
      });
    } else {
      alert("As datas estao em conflito!");
    }
  }

  findAllReservas() {
    this.reservaService.findAll().subscribe((reservas: Reserva[]) => {
      this.reservas = reservas;
    });
  }

  cancelReserva(id: Number) {
    this.reservaService.cancel(id).subscribe(() => {
      this.findAllReservas();
    })
  }

  updateReserva(reserva: Reserva) {
    this.reserva = { ...reserva };
  }

  cleanForm(form: NgForm) {
      this.findAllReservas();
      form.resetForm();
      this.reserva = {} as Reserva;
  }

  verificaDatas() {
      const intervalo1: Interval = { start: parseISO(this.reserva.dataInicio.toString()), end: parseISO(this.reserva.dataFim.toString()) };
      for(let reserva of this.reservas) {
        const intervalo2: Interval = { start: parseISO(reserva.dataInicio.toString()), end: parseISO(reserva.dataFim.toString()) };
        if(areIntervalsOverlapping(intervalo1, intervalo2) && this.reserva.id != reserva.id) return false;
      }
      return true;
  }
}
